package com.ydespreaux.shared.data.autoconfigure.elasticsearch.settings;/** * Cette enumération définit un ensemble d'actions à effectuer sur les templates. * * @since 1.0.0 * @author Yoann Despréaux */public enum TemplateAction {    /**     * Aucune action n'est effectué sur le template     */    NONE,    /**     * Création du template s'il n'existe pas     */    CREATE_ONLY,    /**     * Créer ou met à jour le template     */    CREATE_OR_UPDATE}