package com.ydespreaux.shared.data.autoconfigure.elasticsearch.settings;import lombok.Getter;import lombok.NonNull;import lombok.Setter;import org.springframework.boot.context.properties.ConfigurationProperties;import java.util.Collections;import java.util.List;/** * Sping boot configuration property class for templates. * * @since 1.0.0 * @author yoann.despreaux */@Getter@Setter@ConfigurationProperties(prefix = "spring.elasticsearch.template")public class TemplateProperties {    /**     * Type d'action à effectuer sur chaque template     */    @NonNull    private TemplateAction action = TemplateAction.NONE;    /**     * Liste des scripts décrivant les templates à utiliser.     */    private List<String> scripts = Collections.emptyList();}