package com.ydespreaux.shared.data.elasticsearch.core.scroll;import com.ydespreaux.shared.data.elasticsearch.ScrolledPage;import lombok.Getter;import lombok.Setter;import java.util.ArrayList;import java.util.List;@Getter@Setterpublic class ScrolledPageResult<T> implements ScrolledPage<T> {    /**     *     */    private String scrollId;    /**     *     */    private Long totalElements;    /**     *     */    private List<T> content = new ArrayList<>();    @Override    public Boolean hasContent() {        return this.content != null && !this.content.isEmpty();    }    /**     *     * @param items     * @param totalHits     * @param scrollId     * @param <E>     * @return     */    public static <E> ScrolledPageResult<E> of(List<E> items, Long totalHits, String scrollId) {        ScrolledPageResult<E> page = new ScrolledPageResult<>();        page.setTotalElements(totalHits);        page.setScrollId(scrollId);        page.setContent(items);        return page;    }    /**     * @param element     */    private void addElement(T element) {        if (content == null) {            content = new ArrayList<>();        }        this.content.add(element);    }}