package com.ydespreaux.shared.data.elasticsearch.repository;import com.ydespreaux.shared.data.elasticsearch.core.ElasticsearchOperations;import org.springframework.data.repository.core.EntityInformation;/** * * @param <T> */public class SimpleElasticsearchRepository<T> extends AbstractElasticsearchRepository<T, String> {    /**     * @param metadata     * @param elasticsearchOperations     */    public SimpleElasticsearchRepository(EntityInformation<T, String> metadata, ElasticsearchOperations elasticsearchOperations) {        super(metadata, elasticsearchOperations);    }    /**     *     * @param elasticsearchOperations     */    public SimpleElasticsearchRepository(ElasticsearchOperations elasticsearchOperations) {        super(elasticsearchOperations);    }    /**     * @param id     * @return     */    @Override    protected String stringIdRepresentation(String id) {        return id;    }}