package com.ydespreaux.shared.data.elasticsearch.repository.support;import com.ydespreaux.shared.data.elasticsearch.core.ElasticsearchOperations;/** * * @param <T> */public class SimpleElasticsearchRepository<T> extends AbstractElasticsearchRepository<T, String> {    /**     *     * @param elasticsearchOperations     */    public SimpleElasticsearchRepository(ElasticsearchOperations elasticsearchOperations) {        super(elasticsearchOperations);    }    /**     * @param id     * @return     */    @Override    protected String stringIdRepresentation(String id) {        return id;    }}