package com.ydespreaux.shared.data.elasticsearch.configuration;import com.ydespreaux.shared.data.elasticsearch.ElasticsearchOperations;import com.ydespreaux.shared.data.elasticsearch.repositories.ArticleRepository;import com.ydespreaux.shared.data.elasticsearch.repositories.ArticleRepositoryImpl;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;@Configurationpublic class ElasticsearchArticleConfiguration {    @Bean    public ArticleRepository articleRepository(ElasticsearchOperations elasticsearchOperations) {        return new ArticleRepositoryImpl(elasticsearchOperations);    }}