package com.ydespreaux.shared.data.elasticsearch.configuration;import com.ydespreaux.shared.data.elasticsearch.ElasticsearchOperations;import com.ydespreaux.shared.data.elasticsearch.repositories.BookRepository;import com.ydespreaux.shared.data.elasticsearch.repositories.BookRepositoryImpl;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;@Configurationpublic class ElasticsearchBookConfiguration {    @Bean    public BookRepository bookRepository(ElasticsearchOperations elasticsearchOperations) {        return new BookRepositoryImpl(elasticsearchOperations);    }}