package com.ydespreaux.shared.data.elasticsearch.configuration;import com.ydespreaux.shared.data.elasticsearch.core.ElasticsearchOperations;import com.ydespreaux.shared.data.elasticsearch.repositories.ArticleRepository;import com.ydespreaux.shared.data.elasticsearch.repositories.ArticleRepositoryImpl;import com.ydespreaux.shared.data.elasticsearch.repositories.BookRepository;import com.ydespreaux.shared.data.elasticsearch.repositories.BookRepositoryImpl;import com.ydespreaux.shared.data.elasticsearch.repository.config.EnableElasticsearchRepositories;import org.springframework.boot.autoconfigure.EnableAutoConfiguration;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;@Configuration@EnableAutoConfiguration@EnableElasticsearchRepositories(basePackages = {"com.ydespreaux.shared.data.elasticsearch.repositories"})public class ElasticsearchConfiguration {    @Bean    ArticleRepository articleRepository(ElasticsearchOperations operations){        return new ArticleRepositoryImpl(operations);    }    @Bean    BookRepository bookRepository(ElasticsearchOperations operations){        return new BookRepositoryImpl(operations);    }}