package com.ydespreaux.shared.data.elasticsearch.entities;import com.ydespreaux.shared.data.elasticsearch.annotations.Document;import lombok.*;import org.springframework.data.annotation.Id;import org.springframework.data.annotation.Version;@NoArgsConstructor@AllArgsConstructor@Builder@Getter@Setter@Document(aliasOrIndex = "${spring.elasticsearch.aliases.article}",        type = "article",        indexPattern = "${spring.elasticsearch.index-pattern.article}",        indexTimeBasedSupport = ArticleTimeBasedSupport.class)public class Article {    public enum EnumEntrepot {        E1, E2, E3, UNKNOWN    }    @Id    private String documentId;    @Version    private Long documentVersion;    private String name;    private String description;    private EnumEntrepot entrepot;}