package com.ydespreaux.shared.data.elasticsearch.entities;import com.ydespreaux.shared.data.elasticsearch.annotations.Document;import io.searchbox.annotations.JestId;import io.searchbox.annotations.JestVersion;import lombok.*;@NoArgsConstructor@AllArgsConstructor@Builder@Getter@Setter@Document(aliasOrIndex = "${spring.elasticsearch.aliases.article}",        type = "article",        indexPattern = "${spring.elasticsearch.index-pattern.article}",        indexTimeBasedSupport = ArticleTimeBasedSupport.class)public class Article {    public enum EnumEntrepot {        E1, E2, E3, UNKNOWN    }    @JestId    private String documentId;    @JestVersion    private Integer documentVersion;    private String name;    private String description;    private EnumEntrepot entrepot;}