package com.ydespreaux.shared.data.elasticsearch.entities;import com.ydespreaux.shared.data.elasticsearch.mapping.IndexTimeBasedParameter;import com.ydespreaux.shared.data.elasticsearch.mapping.IndexTimeBasedSupport;public class ArticleTimeBasedSupport extends IndexTimeBasedSupport<Article> {    /**     * Génère le nom d'un index de type time-based en fonction d'une date et d'un document à indexer.     *     * @param indexParameter     * @return     */    @Override    public String buildIndex(IndexTimeBasedParameter<Article> indexParameter) {        return String.format(indexParameter.generateIndexWithTimeEvent(),                indexParameter.getDocument() == null ? Article.EnumEntrepot.UNKNOWN : indexParameter.getDocument().getEntrepot())                .toLowerCase();    }}