package com.ydespreaux.shared.data.elasticsearch.support;import com.ydespreaux.shared.data.autoconfigure.elasticsearch.ElasticsearchDataAutoConfiguration;import com.ydespreaux.shared.data.elasticsearch.ElasticsearchOperations;import com.ydespreaux.shared.data.elasticsearch.configuration.ElasticsearchBookConfiguration;import org.elasticsearch.action.admin.indices.mapping.get.GetMappingsRequest;import org.elasticsearch.action.admin.indices.mapping.get.GetMappingsResponse;import org.elasticsearch.action.admin.indices.settings.get.GetSettingsRequest;import org.elasticsearch.action.admin.indices.settings.get.GetSettingsResponse;import org.elasticsearch.client.RequestOptions;import org.elasticsearch.client.RestHighLevelClient;import org.elasticsearch.common.collect.ImmutableOpenMap;import org.elasticsearch.common.settings.Settings;import org.hamcrest.Matchers;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.autoconfigure.elasticsearch.rest.RestClientAutoConfiguration;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.test.annotation.DirtiesContext;import org.springframework.test.context.junit4.SpringRunner;import static org.hamcrest.Matchers.equalTo;import static org.hamcrest.Matchers.is;import static org.junit.Assert.assertThat;@DirtiesContext@RunWith(SpringRunner.class)@SpringBootTest(classes = {        RestClientAutoConfiguration.class,        ElasticsearchDataAutoConfiguration.class,        ElasticsearchBookConfiguration.class})public class ITBookRepositoryContextTest {    private static final String INDEX_BOOK_NAME = "books";    @Autowired    private ElasticsearchOperations elasticsearchOperations;    @Autowired    private RestHighLevelClient client;    @Test    public void context() throws Exception{        assertThat(this.elasticsearchOperations.indexExists(INDEX_BOOK_NAME), Matchers.is(true));        GetMappingsRequest mappingsRequest = new GetMappingsRequest()                .indices(INDEX_BOOK_NAME);        GetMappingsResponse response = this.client.indices().getMapping(mappingsRequest, RequestOptions.DEFAULT);        assertThat(response.getMappings().containsKey(INDEX_BOOK_NAME), is(true));        assertThat(response.getMappings().get(INDEX_BOOK_NAME).containsKey("book"), is(true));        GetSettingsRequest settingsRequest = new GetSettingsRequest()                .indices(INDEX_BOOK_NAME);        GetSettingsResponse settingsResponse = this.client.indices().getSettings(settingsRequest, RequestOptions.DEFAULT);        ImmutableOpenMap<String, Settings> indexSettings = settingsResponse.getIndexToSettings();        assertThat(indexSettings.containsKey(INDEX_BOOK_NAME), is(true));        Settings settings = indexSettings.get(INDEX_BOOK_NAME);        assertThat(settings.get("index.refresh_interval"), is(equalTo("1s")));        assertThat(settings.get("index.number_of_shards"), is(equalTo("1")));        assertThat(settings.get("index.number_of_replicas"), is(equalTo("1")));        assertThat(settings.get("index.store.type"), is(equalTo("fs")));    }}